export const hsv2hsl=(e,r,n,o=n-n*r/2,t=Math.min(o,1-o))=>[e,t?(n-o)/t:0,o],random=(e,r)=>(r||(r=e,e=0),Math.random()*(r-e)+e),pointOnCurve=(e,r,n,o,t=[0,0],a=[1,1])=>{const V=Math.PI/2,b=V/n;let s=0,h=0;if(e==="lam\xE9"){let m=r/n*V;const u=2/(2+20*o),p=Math.cos(m),M=Math.sin(m);s=Math.sign(p)*Math.abs(p)**u,h=Math.sign(M)*Math.abs(M)**u}else e==="arc"?(h=Math.cos(-Math.PI/2+r*b+o),s=Math.sin(Math.PI/2+r*b-o)):e==="pow"?(s=Math.pow(1-r/n,1-o),h=Math.pow(r/n,1-o)):e==="powY"?(s=Math.pow(1-r/n,o),h=Math.pow(r/n,1-o)):e==="powX"&&(s=Math.pow(r/n,o),h=Math.pow(r/n,1-o));return s=t[0]+Math.min(Math.max(s,0),1)*(a[0]-t[0]),h=t[1]+Math.min(Math.max(h,0),1)*(a[1]-t[1]),[s,h]};export default function P({total:e=3,centerHue:r=0,hueCycle:n=.3,offsetTint:o=.1,offsetShade:t=.1,curveAccent:a=0,tintShadeHueShift:V=.1,curveMethod:b="arc",offsetCurveModTint:s=.03,offsetCurveModShade:h=.03,minSaturationLight:m=[0,0],maxSaturationLight:u=[1,1]}={}){const p=[],M=[],c=[];for(let l=1;l<e+1;l++){const[i,C]=pointOnCurve(b,l,e+1,a,m,u),f=(360+(-180*n+(r+l*(360/(e+1))*n)))%360,x=hsv2hsl(f,i,C);p.push(x);const[y,g]=pointOnCurve(b,l,e+1,a+s,m,u),w=hsv2hsl(f,y,g);M.push([(f+360*V)%360,w[1]-o,w[2]+o]);const[k,I]=pointOnCurve(b,l,e+1,a-h,m,u),d=hsv2hsl(f,k,I);c.push([(360+(f-360*V))%360,d[1]-t,d[2]-t])}return{light:M,dark:c,base:p,all:[...M,...p,...c]}}
